document.addEventListener("DOMContentLoaded", function () {
    const searchInput = document.getElementById("searchInput");
    const booksContainer = document.getElementById("booksContainer");

    // Check if there is a search query passed from the URL (search-results.html)
    const urlParams = new URLSearchParams(window.location.search);
    const query = urlParams.get('q'); // Get the search query from the URL

    if (query) {
        searchInput.value = query; // Pre-fill the search bar with the query
        displaySearchResults(query);
    }

    // Listen for search button click
    document.getElementById("searchButton").addEventListener("click", function (e) {
        e.preventDefault(); // Prevent the form from submitting
        const searchQuery = searchInput.value.trim();
        if (searchQuery) {
            window.location.href = `search-results.html?q=${encodeURIComponent(searchQuery)}`;
        }
    });

    // Function to filter and display books based on search query
    function displaySearchResults(query) {
        const filteredBooks = books.filter(book =>
            book.title.toLowerCase().includes(query.toLowerCase())
        );

        booksContainer.innerHTML = ''; // Clear previous results

        if (filteredBooks.length > 0) {
            filteredBooks.forEach(book => {
                const bookItem = document.createElement("div");
                bookItem.classList.add("book-item");
                bookItem.innerHTML = `
                    <img src="${book.image}" alt="${book.title}">
                    <h3>${book.title}</h3>
                    <p>by ${book.author}</p>
                    <button>Add to Cart</button>
                `;
                booksContainer.appendChild(bookItem);
            });
        } else {
            booksContainer.innerHTML = "<p>No books found for your search.</p>";
        }
    }
});
